mapper.insert:
INSERT INTO pds
(
    item_name,
    description
)
VALUES
(
    #{itemName},
    #{description}
)

mapper.update:
UPDATE pds
SET item_name = #{itemName},
description = #{description}
WHERE item_id = #{itemId}

INSERT INTO pds_attach
(
    full_name,
    item_id
)
VALUES
(
    #{fullName},
    #{itemId}
)
